import React, { Component } from 'react';
import { NavLink, Redirect } from 'react-router-dom';

import web3 from '.././web3';
import contract from '.././contract';

class Home extends Component {
  state = {
    redirectTo: null, // Change the initial value to null
    isAddDataMode: false,
  };

  async handleMetamaskLogin() {
    try {
      // Enable Metamask provider
      await window.ethereum.enable();

      // Get Metamask accounts
      const accounts = await web3.eth.getAccounts();

      // Call your contract's Identify method
      const result = await contract.methods.Identify().call({ from: accounts[0] });

      this.setState({ redirectTo: result }); // Set the state when you have the result
      console.log(result);
    } catch (error) {
      console.error('Metamask login error:', error);
    }
  }

  componentDidUpdate(prevProps, prevState) {
    // Check if redirectTo state has changed
    if (this.state.redirectTo !== prevState.redirectTo) {
      // You can now handle redirection based on the updated state
      switch (this.state.redirectTo) {
        case 1:
          return <Redirect to="/patient" />;
        case 2:
          return <Redirect to="/doctor" />;
        case 3:
          return <Redirect to="/insurancecompany" />;
        case 4:
          return <Redirect to="/chemist" />;
        default:
          return null; // No redirection
      }
    }
  }

  render() {
    return (
      <div id="home-body">
        {/* ... your render code ... */}
        <button
          onClick={() => this.handleMetamaskLogin()}
          className="btn btn-primary"
        >
          <h4>Login via Metamask</h4>
        </button>
      </div>
    );
  }
}

export default Home;




